import React, { useState } from "react";
import "../jass.css";
import Footer from "./footer";
import Header from "./header";
import Navbar from "./navbar";



function Portfolio() {
    const [page, setPage] = useState('about');

    //var pageHtml = "";
    function renderPage() {
        if (page === "about") {
            return (
                <div>
                    <h1>About me</h1>
                    <img src="../assets/Me.JPG" alt="Me"></img>
                    <p>say stuff about me</p>
                </div>

            )
        }
    }
    const handlePageChange=(newPage)=> { setPage(newPage) }



    return (

        <body>


<div class="navbar">
        <a href="#about" onClick={handlePageChange('about')}>About Me</a>
        <a href="#portfolio" onClick={setPage("portfolio")}>Portfolio</a>
        <a href="#contact" onClick={setPage("contact")}>Contact Me</a>
        <a href="#resume" onClick={setPage("resume")}>Resume</a>
        
    </div>

            
      {/* <Navbar currentPage={page} changePage={handlePageChange()}/> */}
            <Header currentPage={page} renderPage={handlePageChange} />
            <div class="project">
                {renderPage}
            </div>


            <Footer />
        </body>
    )
}

export default Portfolio;
